<?phpclass Connect{	public $PDO;	public $lastInsertItem;	public $lastInsertPhoto;	public function __construct() 	{		$dbhost = "localhost";		$dbname = "getitcooked";		$dbusername ="root";		$dbpassword ="";		$this->PDO = new PDO('mysql:host='.$dbhost.';dbname='.$dbname, $dbusername, $dbpassword );   }	public function addItem($shortname,$longname,$description) 	{		$PDOStatement  = $this->PDO->prepare("INSERT INTO `tbl_content` (`shortname`, `longname`, `description`) VALUES (:shortname , :longname, :description)");		$PDOStatement->bindParam(':shortname',$shortname, PDO::PARAM_STR);		$PDOStatement->bindParam(':longname',$longname, PDO::PARAM_STR);		$PDOStatement->bindParam(':description',$description, PDO::PARAM_STR);		$PDOStatement->execute();		return $this->PDO->lastInsertId();	}      	public function getItems()     {		$PDOStatement  = $this->PDO->prepare("select * from tbl_content ");		$PDOStatement->execute();		$items = $PDOStatement->fetchAll(PDO::FETCH_BOTH);		return $items;	}	public function getItem($id)     {		$PDOStatement  = $this->PDO->prepare("select * from tbl_content WHERE `id` = :id");		$PDOStatement->bindParam(':id', $id, PDO::PARAM_INT);		$PDOStatement->execute();		$items = $PDOStatement->fetchObject();		return $items;	}			public function addImage($small,$content_id,$large,$medium) 	{		$PDOStatement  = $this->PDO->prepare("INSERT INTO `tbl_picture`(`small`,`content_id`,`large`,`medium`) VALUES (:small,:content_id,:large,:medium)");		$PDOStatement->bindParam(':small',$small, PDO::PARAM_LOB);		$PDOStatement->bindParam(':large',$large, PDO::PARAM_LOB);		$PDOStatement->bindParam(':medium',$medium, PDO::PARAM_LOB);		$PDOStatement->bindParam(':content_id',$content_id, PDO::PARAM_INT);		$PDOStatement->execute();		return $this->PDO->lastInsertId();   	}	public function getImageById($id) 	{		$PDOStatement  = $this->PDO->prepare("SELECT * FROM `tbl_picture` WHERE `id` = :id");		$PDOStatement->bindParam(':id', $id, PDO::PARAM_INT);		$PDOStatement->execute();			return $PDOStatement->fetchObject();	   	}		public function getImagesByContentId($content_id) 	{		$PDOStatement  = $this->PDO->prepare("SELECT `id` FROM `tbl_picture` WHERE `content_id` = :content_id");		$PDOStatement->bindParam(':content_id', $content_id, PDO::PARAM_INT);		$PDOStatement->execute();			$PDOStatement->bindColumn(2, $small, PDO::PARAM_LOB);		$small = $PDOStatement->fetchAll(PDO::FETCH_BOTH);		return $small;   	}}?>